import React, { useState, useMemo } from 'react';
import ProductCard from '../components/ProductCard';
import ProductFilter from '../components/ProductFilter';
import { mockProducts, priceRanges } from '../data/mockData';

function HomePage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedPriceRange, setSelectedPriceRange] = useState('all');

  const filteredProducts = useMemo(() => {
    let filtered = mockProducts;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by price range
    if (selectedPriceRange !== 'all') {
      const priceRange = priceRanges.find(range => range.id === selectedPriceRange);
      if (priceRange) {
        filtered = filtered.filter(product => 
          product.price >= priceRange.min && product.price <= priceRange.max
        );
      }
    }

    return filtered;
  }, [searchTerm, selectedCategory, selectedPriceRange]);

  return (
    <div style={{
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px'
    }}>
      {/* Hero Section */}
      <div style={{
        backgroundColor: '#3498db',
        color: 'white',
        padding: '60px 40px',
        borderRadius: '12px',
        textAlign: 'center',
        marginBottom: '30px',
        backgroundImage: 'linear-gradient(135deg, #3498db 0%, #2980b9 100%)'
      }}>
        <h1 style={{
          margin: '0 0 16px 0',
          fontSize: '3rem',
          fontWeight: 'bold'
        }}>
          Welcome to E-Store
        </h1>
        <p style={{
          margin: 0,
          fontSize: '1.3rem',
          opacity: 0.9
        }}>
          Discover amazing products at unbeatable prices
        </p>
      </div>

      {/* Filter Section */}
      <ProductFilter
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
        selectedPriceRange={selectedPriceRange}
        onPriceRangeChange={setSelectedPriceRange}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
      />

      {/* Results Summary */}
      <div style={{
        marginBottom: '20px',
        padding: '16px',
        backgroundColor: '#ecf0f1',
        borderRadius: '8px'
      }}>
        <p style={{
          margin: 0,
          color: '#2c3e50',
          fontSize: '1.1rem'
        }}>
          Showing {filteredProducts.length} product{filteredProducts.length !== 1 ? 's' : ''}
          {searchTerm && ` for "${searchTerm}"`}
          {selectedCategory !== 'all' && ` in ${selectedCategory}`}
        </p>
      </div>

      {/* Products Grid */}
      {filteredProducts.length > 0 ? (
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px'
        }}>
          {filteredProducts.map(product => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      ) : (
        <div style={{
          textAlign: 'center',
          padding: '60px 20px',
          backgroundColor: '#f8f9fa',
          borderRadius: '12px'
        }}>
          <div style={{ fontSize: '4rem', marginBottom: '16px' }}>üîç</div>
          <h3 style={{
            color: '#6c757d',
            margin: '0 0 8px 0'
          }}>
            No products found
          </h3>
          <p style={{
            color: '#6c757d',
            margin: 0
          }}>
            Try adjusting your search or filter criteria
          </p>
        </div>
      )}
    </div>
  );
}

export default HomePage;
