import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useCart } from "../context/cart-context";
import { mockProducts } from "../data/fake-data";
import "./ProductDetailPage.css";

function ProductDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart, cartItems } = useCart();

  const product = mockProducts.find((p) => p.id === parseInt(id));

  if (!product) {
    return (
      <div className="product-not-found">
        <h2 className="not-found-heading">Product Not Found</h2>
        <p className="not-found-text">
          The product you're looking for doesn't exist.
        </p>
        <button onClick={() => navigate("/")} className="back-to-products-btn">
          Back to Products
        </button>
      </div>
    );
  }

  const handleAddToCart = () => {
    addToCart(product);
  };

  const isInCart = cartItems.some((item) => item.id === product.id);
  const cartItem = cartItems.find((item) => item.id === product.id);

  return (
    <div className="product-detail-container">
      {/* Breadcrumb */}
      <div className="breadcrumb">
        <span onClick={() => navigate("/")} className="breadcrumb-link">
          Home
        </span>
        <span className="breadcrumb-separator">/</span>
        <span className="breadcrumb-category">{product.category}</span>
        <span className="breadcrumb-separator">/</span>
        <span className="breadcrumb-current">{product.name}</span>
      </div>

      <div className="product-content">
        {/* Product Image */}
        <div className="product-image-section">
          <img
            src={product.image}
            alt={product.name}
            className="product-image"
          />
        </div>

        {/* Product Info */}
        <div className="product-info-section">
          <div className="product-badges">
            <span className="product-category-badge">{product.category}</span>
          </div>

          <h1 className="product-name">{product.name}</h1>

          <div className="product-price">${product.price.toLocaleString()}</div>

          <div className="product-description-box">
            <h3 className="description-heading">Description</h3>
            <p className="description-text">{product.description}</p>
          </div>

          {/* Add to Cart Section */}
          <div className="add-to-cart-actions">
            <button
              onClick={handleAddToCart}
              className={`add-to-cart-btn ${isInCart ? "in-cart" : ""}`}
            >
              {isInCart ? `In Cart (${cartItem?.quantity})` : "Add to Cart"}
            </button>
            {isInCart && (
              <span className="cart-added-message">✓ Added to cart</span>
            )}
          </div>

          {/* Action Buttons */}
          <div className="secondary-actions">
            <button onClick={() => navigate("/cart")} className="view-cart-btn">
              View Cart
            </button>
            <button
              onClick={() => navigate("/")}
              className="continue-shopping-btn"
            >
              Continue Shopping
            </button>
          </div>
        </div>
      </div>

      {/* Additional Product Info */}
      <div className="product-features-section">
        <h3 className="features-heading">Why buy from us?</h3>
        <div className="features-grid">
          <div className="feature-item">
            <div className="feature-icon">🚚</div>
            <h4 className="feature-title">Fast Shipping</h4>
            <p className="feature-description">Orders over $50 ship free.</p>
          </div>
          <div className="feature-item">
            <div className="feature-icon">🔄</div>
            <h4 className="feature-title">Easy Returns</h4>
            <p className="feature-description">Hassle-free 30-day returns.</p>
          </div>
          <div className="feature-item">
            <div className="feature-icon">🛡️</div>
            <h4 className="feature-title">Quality Guarantee</h4>
            <p className="feature-description">1-year manufacturer warranty.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetailPage;
