import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

function CartPage() {
  const { 
    cartItems, 
    updateQuantity, 
    removeFromCart, 
    getTotalPrice, 
    getTotalItems 
  } = useCart();

  if (cartItems.length === 0) {
    return (
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        padding: '40px 20px',
        textAlign: 'center'
      }}>
        <div style={{
          backgroundColor: 'white',
          padding: '60px 40px',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <div style={{ fontSize: '4rem', marginBottom: '20px' }}>üõí</div>
          <h2 style={{
            color: '#2c3e50',
            margin: '0 0 16px 0'
          }}>
            Your cart is empty
          </h2>
          <p style={{
            color: '#7f8c8d',
            marginBottom: '30px'
          }}>
            Looks like you haven't added any items to your cart yet.
          </p>
          <Link
            to="/"
            style={{
              display: 'inline-block',
              padding: '12px 30px',
              backgroundColor: '#3498db',
              color: 'white',
              textDecoration: 'none',
              borderRadius: '8px',
              fontSize: '1.1rem',
              fontWeight: '600',
              transition: 'background-color 0.3s'
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = '#2980b9'}
            onMouseOut={(e) => e.target.style.backgroundColor = '#3498db'}
          >
            üõçÔ∏è Continue Shopping
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div style={{
      maxWidth: '1000px',
      margin: '0 auto',
      padding: '20px'
    }}>
      <h1 style={{
        color: '#2c3e50',
        marginBottom: '30px',
        fontSize: '2.5rem'
      }}>
        üõí Shopping Cart
      </h1>

      <div style={{
        display: 'grid',
        gridTemplateColumns: '1fr 350px',
        gap: '30px'
      }}>
        {/* Cart Items */}
        <div>
          {cartItems.map(item => (
            <div
              key={item.id}
              style={{
                backgroundColor: 'white',
                padding: '20px',
                borderRadius: '12px',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                marginBottom: '16px',
                display: 'flex',
                gap: '20px',
                alignItems: 'center'
              }}
            >
              <img
                src={item.image}
                alt={item.name}
                style={{
                  width: '100px',
                  height: '100px',
                  objectFit: 'cover',
                  borderRadius: '8px'
                }}
              />

              <div style={{ flex: 1 }}>
                <h3 style={{
                  margin: '0 0 8px 0',
                  color: '#2c3e50'
                }}>
                  {item.name}
                </h3>
                <p style={{
                  margin: '0 0 12px 0',
                  color: '#7f8c8d',
                  fontSize: '0.9rem'
                }}>
                  {item.description}
                </p>
                <div style={{
                  fontSize: '1.2rem',
                  fontWeight: 'bold',
                  color: '#27ae60'
                }}>
                  ${item.price}
                </div>
              </div>

              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px'
              }}>
                <button
                  onClick={() => updateQuantity(item.id, item.quantity - 1)}
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '50%',
                    border: '2px solid #e74c3c',
                    backgroundColor: 'white',
                    color: '#e74c3c',
                    cursor: 'pointer',
                    fontSize: '1.2rem',
                    fontWeight: 'bold'
                  }}
                >
                  -
                </button>

                <span style={{
                  fontSize: '1.2rem',
                  fontWeight: 'bold',
                  minWidth: '30px',
                  textAlign: 'center'
                }}>
                  {item.quantity}
                </span>

                <button
                  onClick={() => updateQuantity(item.id, item.quantity + 1)}
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '50%',
                    border: '2px solid #27ae60',
                    backgroundColor: 'white',
                    color: '#27ae60',
                    cursor: 'pointer',
                    fontSize: '1.2rem',
                    fontWeight: 'bold'
                  }}
                >
                  +
                </button>

                <button
                  onClick={() => removeFromCart(item.id)}
                  style={{
                    padding: '8px 12px',
                    backgroundColor: '#e74c3c',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    marginLeft: '12px'
                  }}
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Order Summary */}
        <div style={{
          backgroundColor: 'white',
          padding: '24px',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
          height: 'fit-content',
          position: 'sticky',
          top: '20px'
        }}>
          <h3 style={{
            margin: '0 0 20px 0',
            color: '#2c3e50',
            fontSize: '1.4rem'
          }}>
            üìã Order Summary
          </h3>

          <div style={{
            borderBottom: '1px solid #ecf0f1',
            paddingBottom: '16px',
            marginBottom: '16px'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              marginBottom: '8px'
            }}>
              <span>Items ({getTotalItems()})</span>
              <span>${getTotalPrice().toFixed(2)}</span>
            </div>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              marginBottom: '8px'
            }}>
              <span>Shipping</span>
              <span>FREE</span>
            </div>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              marginBottom: '8px'
            }}>
              <span>Tax</span>
              <span>${(getTotalPrice() * 0.08).toFixed(2)}</span>
            </div>
          </div>

          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            fontSize: '1.3rem',
            fontWeight: 'bold',
            color: '#2c3e50',
            marginBottom: '24px'
          }}>
            <span>Total</span>
            <span>${(getTotalPrice() * 1.08).toFixed(2)}</span>
          </div>

          <Link
            to="/checkout"
            style={{
              display: 'block',
              width: '100%',
              padding: '16px',
              backgroundColor: '#27ae60',
              color: 'white',
              textDecoration: 'none',
              borderRadius: '8px',
              textAlign: 'center',
              fontSize: '1.1rem',
              fontWeight: '600',
              marginBottom: '12px',
              transition: 'background-color 0.3s'
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = '#229954'}
            onMouseOut={(e) => e.target.style.backgroundColor = '#27ae60'}
          >
            üí≥ Proceed to Checkout
          </Link>

          <Link
            to="/"
            style={{
              display: 'block',
              width: '100%',
              padding: '12px',
              backgroundColor: 'transparent',
              color: '#3498db',
              textDecoration: 'none',
              borderRadius: '8px',
              textAlign: 'center',
              fontSize: '1rem',
              border: '2px solid #3498db',
              transition: 'all 0.3s'
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#3498db';
              e.target.style.color = 'white';
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#3498db';
            }}
          >
            üõçÔ∏è Continue Shopping
          </Link>
        </div>
      </div>
    </div>
  );
}

export default CartPage;
