import React, { useState, useMemo } from "react";
import ProductCard from "../components/product-card";
import ProductFilter from "../components/product-filter";
import { mockProducts, priceRanges } from "../data/fake-data";

function HomePage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedPriceRange, setSelectedPriceRange] = useState("all");

  const filteredProducts = useMemo(() => {
    let filtered = mockProducts;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(
        (product) =>
          product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          product.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by category
    if (selectedCategory !== "all") {
      filtered = filtered.filter(
        (product) => product.category === selectedCategory
      );
    }

    // Filter by price range
    if (selectedPriceRange !== "all") {
      const priceRange = priceRanges.find(
        (range) => range.id === selectedPriceRange
      );
      if (priceRange) {
        filtered = filtered.filter(
          (product) =>
            product.price >= priceRange.min && product.price <= priceRange.max
        );
      }
    }

    return filtered;
  }, [searchTerm, selectedCategory, selectedPriceRange]);

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#f0f4f8",
        fontFamily:
          "'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif",
      }}
    >
      {/* Hero Section */}
      <div
        style={{
          background: "linear-gradient(135deg, #1e3a8a 0%, #3b82f6 100%)",
          color: "white",
          padding: "80px 20px",
          textAlign: "center",
          boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
        }}
      >
        <div style={{ maxWidth: "800px", margin: "0 auto" }}>
          <h1
            style={{
              fontSize: "3rem",
              fontWeight: "700",
              marginBottom: "1rem",
              letterSpacing: "1px",
            }}
          >
            BCU Store Premium
          </h1>
          <p
            style={{
              fontSize: "1.2rem",
              fontWeight: "300",
              lineHeight: "1.6",
              maxWidth: "600px",
              margin: "0 auto",
            }}
          >
            Discover a curated collection of premium products, from
            state-of-the-art electronics to elegant home goods. Quality and
            style, delivered.
          </p>
        </div>
      </div>

      <div
        style={{
          maxWidth: "1400px",
          margin: "0 auto",
          padding: "40px 20px",
        }}
      >
        {/* Filter Section */}
        <ProductFilter
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
          selectedPriceRange={selectedPriceRange}
          onPriceRangeChange={setSelectedPriceRange}
          searchTerm={searchTerm}
          onSearchChange={setSearchTerm}
        />

        {/* Results Summary */}
        <div
          style={{
            marginBottom: "30px",
            padding: "20px",
            backgroundColor: "white",
            borderRadius: "12px",
            boxShadow: "0 2px 10px rgba(0, 0, 0, 0.05)",
            borderLeft: "4px solid #3b82f6",
          }}
        >
          <p
            style={{
              margin: 0,
              color: "#1e3a8a",
              fontSize: "1.1rem",
              fontWeight: "600",
              textAlign: "center",
            }}
          >
            {filteredProducts.length > 0 ? (
              <>
                <span style={{ marginRight: "8px" }}>üì¶</span>
                Showing <strong>{filteredProducts.length}</strong> product
                {filteredProducts.length !== 1 ? "s" : ""}
                {searchTerm && ` for "${searchTerm}"`}
                {selectedCategory !== "all" &&
                  ` in the ${selectedCategory} category`}
              </>
            ) : (
              <>
                <span style={{ marginRight: "8px" }}>üîç</span>
                No products found matching your criteria.
              </>
            )}
          </p>
        </div>

        {/* Products Grid */}
        {filteredProducts.length > 0 ? (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
              gap: "30px",
              paddingBottom: "60px",
            }}
          >
            {filteredProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        ) : (
          <div
            style={{
              textAlign: "center",
              padding: "80px 20px",
              backgroundColor: "white",
              borderRadius: "12px",
              boxShadow: "0 2px 10px rgba(0, 0, 0, 0.05)",
              margin: "50px 0",
            }}
          >
            <div style={{ fontSize: "4rem", marginBottom: "20px" }}>üò¢</div>
            <h3
              style={{
                color: "#4a5568",
                margin: "0 0 10px 0",
                fontSize: "1.8rem",
              }}
            >
              No products found
            </h3>
            <p
              style={{
                color: "#718096",
                margin: "0 0 30px 0",
                fontSize: "1.1rem",
                maxWidth: "500px",
                margin: "0 auto 30px auto",
              }}
            >
              It seems there are no products that match your current filters.
              Please try a different search or broaden your criteria.
            </p>
            <button
              onClick={() => {
                setSearchTerm("");
                setSelectedCategory("all");
                setSelectedPriceRange("all");
              }}
              style={{
                backgroundColor: "#3b82f6",
                color: "white",
                border: "none",
                padding: "12px 28px",
                borderRadius: "8px",
                fontSize: "1rem",
                fontWeight: "600",
                cursor: "pointer",
                transition: "background-color 0.3s ease",
              }}
              onMouseOver={(e) => (e.target.style.backgroundColor = "#2563eb")}
              onMouseOut={(e) => (e.target.style.backgroundColor = "#3b82f6")}
            >
              üîÑ Reset All Filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;
