import React from 'react';
import { useCart } from '../context/CartContext';

function ProductCard({ product }) {
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    addToCart(product);
  };

  return (
    <div style={{
      border: '1px solid #e0e0e0',
      borderRadius: '12px',
      padding: '16px',
      backgroundColor: 'white',
      boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
      transition: 'all 0.3s ease',
      cursor: 'pointer'
    }}
    onMouseOver={(e) => {
      e.currentTarget.style.transform = 'translateY(-5px)';
      e.currentTarget.style.boxShadow = '0 8px 25px rgba(0,0,0,0.15)';
    }}
    onMouseOut={(e) => {
      e.currentTarget.style.transform = 'translateY(0)';
      e.currentTarget.style.boxShadow = '0 4px 6px rgba(0,0,0,0.1)';
    }}
    >
      <div style={{
        width: '100%',
        height: '200px',
        borderRadius: '8px',
        overflow: 'hidden',
        marginBottom: '12px'
      }}>
        <img 
          src={product.image} 
          alt={product.name}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover'
          }}
        />
      </div>

      <h3 style={{
        margin: '0 0 8px 0',
        fontSize: '1.2rem',
        fontWeight: '600',
        color: '#2c3e50'
      }}>
        {product.name}
      </h3>

      <p style={{
        margin: '0 0 12px 0',
        color: '#7f8c8d',
        fontSize: '0.9rem',
        lineHeight: '1.4'
      }}>
        {product.description}
      </p>

      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '16px'
      }}>
        <span style={{
          fontSize: '1.4rem',
          fontWeight: 'bold',
          color: '#27ae60'
        }}>
          ${product.price}
        </span>
        <span style={{
          backgroundColor: '#ecf0f1',
          color: '#2c3e50',
          padding: '4px 12px',
          borderRadius: '20px',
          fontSize: '0.8rem',
          textTransform: 'capitalize'
        }}>
          {product.category}
        </span>
      </div>

      <button
        onClick={handleAddToCart}
        style={{
          width: '100%',
          padding: '12px',
          backgroundColor: '#3498db',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontSize: '1rem',
          fontWeight: '600',
          cursor: 'pointer',
          transition: 'background-color 0.3s ease'
        }}
        onMouseOver={(e) => e.target.style.backgroundColor = '#2980b9'}
        onMouseOut={(e) => e.target.style.backgroundColor = '#3498db'}
      >
        ðŸ›’ Add to Cart
      </button>
    </div>
  );
}

export default ProductCard;
